        return this.props.data.map(elem=>(
            <li className="list-item"><img className="clubllogo" src={elem.logo } />&nbsp;&nbsp;{elem.name}</li>
        ))
    }

  render() {

    return (
      <div>

<div class="card groupCard">
  <header class="card-header">
    <p class="card-header-title" style={{"backgroundColor":GroupColor[this.props.index]}}>
      Group {GroupName[this.props.index]}
    </p>
  </header>
  <div class="card-content">
    <ul className="lifst">
   {this.renderList()}
    </ul>
    </div>
  {/* </div> */}
</div>
      </div>
    )
  }
}
