import React, { Component } from 'react'
import { Layout } from 'antd';
import GroupCard from './card'
import Nav from './nav'
import _ from 'lodash'
import { Menu, Icon, Button } from 'antd';
const { Header, Content, Footer } = Layout;
const data = require('./data.json')

export default class dashboard extends Component {
    state = {
        groups: [],
        loaded: false
    }

    generateGroups = () => {
        const ucl = data.concat()
        const shuffle = (array) => {
            var currentIndex = array.length, temporaryValue, randomIndex;
            // While there remain elements to shuffle...
            while (0 !== currentIndex) {
                // Pick a remaining element...
                randomIndex = Math.floor(Math.random() * currentIndex);
                currentIndex -= 1;
                // And swap it with the current element.
                temporaryValue = array[currentIndex];
                array[currentIndex] = array[randomIndex];
                array[randomIndex] = temporaryValue;
            }

            return array;
        }


        shuffle(ucl)

        function getGroups() {
            const group = []

            // cmaking an array of winners
            const winners = []
            ucl.forEach(element => {
                if (element.winner) {
                    winners.push(element)
                }
            });
            for (let x in winners) {
                let tempArr = []
                let countryArr = []
                tempArr.push(winners[x])
                countryArr.push(winners[x].country)
                // console.log("winner",winners[x]);
                // console.log("country arr before loop",countryArr);
                for (let y in ucl) {
                    let count = 0
                    while (tempArr.length < 4) {
                        count = count + 1
                        const next = ucl[Math.floor(Math.random(0, ucl.length) * ucl.length)]
                        // console.log("next generated",next);
                        const countryCount = countryArr.filter(el => {
                            return el === next.country
                        })
                        // console.log(countryArr);
                        // console.log("countryValidation",countryCount);
                        if (next.winner !== true && (countryCount.length === 0)) {
                            // console.log("pushed");
                            tempArr.push(next)
                            _.pullAllBy(ucl, [{ 'name': next.name }], 'name');
                            countryArr.push(next.country)
                            console.log(ucl.length);

                        }
                        if (count > 10) {
                            break
                        }
                    }
                    // console.log("temp ha ye", group.push(tempArr));
                    group.push(tempArr)
                    break
                }
            }

            let totalTeams=0
            group.map(el=>{
                el.map(elem=>{
                    totalTeams =totalTeams+1

                })
            })

            if (totalTeams<32) {
                return -1
            }
            else {
                return group
            }
        }
        return getGroups()
    }
    componentDidMount() {
        let final = this.generateGroups()
        console.log(final)
        if (final === -1) {
            this.componentDidMount()
            console.log(final);

        } else {
            this.setState({
                groups: final,
                loaded: true
            })
        }
    }

    renderGroups = () => {
        return this.state.groups.map((elem, i) => (
            <GroupCard data={elem} index={i} key={i} className="box" ></GroupCard>
        ))
    }
    onclick = () => {
       this.componentDidMount()
    }
    render() {
        //component won't render until the data is fetched
        console.log(this.state);

        return (
            <div>
                <Layout>
                    <Nav>
                        <Menu
                            mode="horizontal"
                        >
                            <Menu.Item>
                                <Button onClick={() => this.onclick()}>Re -Draw</Button></Menu.Item>
                        </Menu>

                    </Nav>

                    <Content style={{ background: '#fff', padding: 24, marginTop: 35, minHeight: 280 }}>

                        <div className="drawGrid">{

                        }
                            {this.state.loaded && this.renderGroups()}
                        </div>
                    </Content>
                </Layout>
            </div>
        )
    }
}
