import React, { Component } from 'react'
import { Layout } from 'antd';
import { Card } from 'antd';
import GroupCard from './card'
import Nav from './nav'
import Axios from 'axios';
const { Content } = Layout;
export default class dashboard extends Component {
    state = {
        groups: [],
        loaded: false
    }
    async componentDidMount(){
        await Axios.get("http://localhost:5000/api/items")
        .then(res=>
            this.setState({
                groups:res.data,
                loaded:true
        }))
        .then(r=>console.log(r))
    }
    renderGroups = ()=>{
        return this.state.groups.map((elem,i)=>(
            <GroupCard data={elem} key={i} className="box" ></GroupCard>
        ))
    }
    render() {
        //component won't render until the data is fetched
        console.log(this.state);

        return (
            <div>

                    <Layout>
                        <Nav></Nav>
                        <Content style={{ background: '#fff', padding: 24, marginTop: 35, minHeight: 280 }}>

                        <div className="drawGrid">{

                        }
                        {this.state.loaded && this.renderGroups()}
                        </div>
                        </Content>
                    </Layout>
            </div>
        )
    }
}
